import React from 'react'
import {useState, useEffect} from 'react'
import {useNavigate, useParams} from 'react-router-dom'
import {getBlog, deleteBlog} from '../services/getApis'

export default function Show() {
    const nav = useNavigate
    const {id} = useParams()
    const [text, setText] = useState({})

    useEffect(() => {
        getBlog(id)
        .then(res => setText(res.data))
    })

    const deleteTheBlog = () => {
        deleteBlog(id)
        nav('/')
    }

    return (
        <div className="show">
            <h1>Show Page</h1>
            <article>
            <ul className="art">
            {posts.map((post) => {
              return( 
                  <li>
                <h2 className="index2"><a href={`/${post._id}`}> {post.title} </a></h2>
                 <div>Written by: {post.author}</div>
                 <p>{post.body}</p>

                </li>
                    )
                })}
        </ul>
              <h2 className="show2"> {text.title} </h2>
                <div>Written by: {text.author}</div>
                <p>{text.body}</p>
                {comments.map((comment) => {
                         return (
                             <div>
                               <div>{comment.cName} </div>
                               <div>{comment.cMessage} </div>
                               <div>{comment.cDate} </div>
                             </div>
                         )
                     })}
                <button onClick={() => {nav(`/${id}/edit`)}}> Edit Blog</button> <br/>
                <button onClick={deleteTheBlog}>Delete</button>     
            </article>
        </div>
    )
}
