import '../App.css'
import {createBlog} from '../services/getApis'
import {useNavigate} from 'react-router-dom'
import {useState} from 'react'

export default function Create() {
    const nav = useNavigate()
    const [title, setTitle] = useSate('')
    const [createdAt, setCreatedAt] = useSate('')
    const createTheBlog = e => {
        const blog = {
            title: e.target.title.value,
            createdAt: e.target.createdAt.value,
        }
        console.log(blog)
        createBlog(blog)
        nav('/')
    }

    return (
        <div>
             <h1 className="crb">Create Blog</h1>
            <form onSubmit={createTheBlog} id="eblog">
                Title: <input type="text" name="title" /> <br/>
                CreatedAt: <input type="text" required value={createdAt} onChange={(e) => set(e.target.value)} /> <br/> 
                    <input type='submit' value='New Blog' />
            </form>           
        </div>
    )
}
