import '../App.css'
import {getBlog, createBlog} from '../services/getApis'
import {useNavigate} from 'react-router-dom'
//import {useState, useEffect} from 'react'

export default function Create() {
    const nav = useNavigate()
    // const [title, setTitle] = useState('')
    // const [createdAt, setCreatedAt] = useState('')
    // const [image, setImage] = useState('')

    const createTheBlog = e => {
        const blog = {
            title: e.target.title.value,
            image: e.target.image.value,
            createdAt: e.target.createdAt.value,
        }
        console.log(blog)
        createBlog(blog)
        nav('/')
    }

    // useEffect(() => {
    //     getBlog()

    //     .then(res => {
    //         setTitle(res.data.title)
    //         setImage(res.data.image)
    //         setCreatedAt(res.data.createdAt)

    //     })

    // }, [])

    return (
        <div>
             <h1 className="crb">Create Blog</h1>
            <form onSubmit={createTheBlog} id="eblog">
            Title: <input type='text' name='title' /> <br/>
               Image: <input type='text' name='albumTitle' /> <br/>
               CoverArt <input type='text' name='coverArt' /> <br/>
                    <input type='submit' value='New Blog' />
            </form>           
        </div>
    )
}
