import '../App.css'
import {useNavigate, useParams} from 'react-router-dom'
import {React, useState, useEffect} from 'react'
import { editBlog, getPost} from '../services/getApis'

export default function Edit() {
    const {id} = useParams()
    const nav = useNavigate()
    const [data, setData] = useState({})

    useEffect(() => {
        getPost(id)
        .then(res => setData(res.data))
    }, [])

    const editTheBlog = e => {
        e.preventDefault()
        console.log("editBlog")
    }

    return (
        <div>
            <h1>Edit Blog</h1>
            <form onSubmit={editTheBlog} id="edblog">
                Title: <input type="text" name="title" defaultValue={data.title} ></input> <br/>
                Author: <input type="text" name="author" defaultValue={data.author} ></input> <br/>
                Body:<input type="text" name="body" defaultValue={data.body} ></input> <br/>
                Comment:<input type="text" name="comment" defaultValue={data.comment} ></input> 
                    <input type='submit' value='Update Blog' />

            </form>
        </div>
    )
}
